import React from 'react'

import Conditional from './Conditional'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            unreadMessages: []
        }
    }

    render() {
        return (
            <div>
                {
                    this.state.unreadMessages.length && 
                    <h2>You have {this.state.unreadMessages} unread messages!</h2>
                }
            </div>
        )
    }
}

export default App
