import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            loading: false,
            character: {}
        }
    }
    
    componentDidMount() {
        fetch("https://swapi.dev/api/people/1")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    loading: true,
                    character: data
                })
            })
    }

    render() {
        return(
            <div>
                {this.state.character.name}
            </div>
        )
    }
}

export default App
