import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: ""
        }
        //this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        this.setState({
            firstName: event.target.value
        })
    }

    render() {
        return (
            <form>
                <input type="text" placeholder="First Name" onChange={this.handleChange} />
            </form>
        )
    }
}

export default App
