import React from 'react'

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            character: {}
        }
    }
    
    componentDidMount() {
        fetch("https://swapi.dev/api/people/1")
            .then(response => response.json())
            .then(data => {
                this.setState((prevState) => {
                    prevState.character = data
                })
            })
    }

    render() {
        return(
            <div>
                Code goes here  
            </div>
        )
    }
}

export default App
